import { Component } from '@angular/core';
import { TaskService } from 'src/app/services/task/task.service';
import { Task } from 'src/app/task';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css'],
})
export class HomeComponent {

  todoList: Task[] = [];

  nonUrgentTaskExists: boolean = false;
  urgentTaskExists: boolean = false;

  constructor(private taskService: TaskService) {}

  ngOnInit() {
    this.todoList = this.getToDoList();
    this.checkTasksPriority(this.todoList);
  }

  getToDoList(): Task[] {
    return this.taskService.getUnDoneTasks();
  }

  checkTasksPriority(todoList: Task[]) {
    if(todoList.map(item => item.isUrgent).includes(true)) {
      this.urgentTaskExists = true;
    } else {
      this.urgentTaskExists = false;
    }
    if(todoList.map(item => item.isUrgent).includes(false)) {
      this.nonUrgentTaskExists = true;
    } else {
      this.nonUrgentTaskExists = false;
    }
  }

  setTaskAsDone(task: Task) {
    this.taskService.setAsDone(task);
    this.todoList = this.getToDoList();
    this.checkTasksPriority(this.todoList);
  }

  filterCategories(tasklist : Task[]) {
    this.todoList = tasklist;
    this.checkTasksPriority(this.todoList)
  }

}
<section>

    <app-filter (changeCategory)="filterCategories($event)"></app-filter>

<div class="fade-animation" *ngIf="todoList.length > 0; else emptyToDoList">

    <!-- Tâches urgentes -->
    <p *ngIf="this.urgentTaskExists">⚠️ Tâches urgentes</p>
    <ul>
        <ng-container *ngFor="let task of todoList">
            <li *ngIf="task.isUrgent">
                <button class="empty-checkbox" (click)="setTaskAsDone(task)"><svg class="feather feather-check" fill="none" stroke="currentColor" stroke-linecap="round" stroke-linejoin="round" stroke-width="2" viewBox="0 0 24 24"><polyline points="20 6 9 17 4 12"/></svg></button>
                <a routerLink="/update-task/{{task.id}}">
                    {{task.category! | categoryEmoji}}
                    {{task.content}}
                </a>
            </li>
        </ng-container>
    </ul>

    <!-- Tâches non-urgentes -->
    <p *ngIf="this.nonUrgentTaskExists">🌸 Autres tâches</p>
    <ul>
        <ng-container *ngFor="let task of todoList">
            <li *ngIf="!task.isUrgent">
                <button class="empty-checkbox" (click)="setTaskAsDone(task)"><svg class="feather feather-check" fill="none" stroke="currentColor" stroke-linecap="round" stroke-linejoin="round" stroke-width="2" viewBox="0 0 24 24"><polyline points="20 6 9 17 4 12"/></svg></button>
                <a routerLink="/update-task/{{task.id}}">
                    {{task.category! | categoryEmoji}}
                    {{task.content}}
                </a>
            </li>
        </ng-container>
    </ul>

</div>

<ng-template #emptyToDoList >

    <div class="empty-home">
        <p>Aucune tâche pour le moment 🙌</p>
        <a class="blue-button" routerLink="/new-task">Créer une tâche</a>
    </div>

</ng-template>

</section>
